
function topMenuAddFill(){const topMenu=document.querySelector('.topMenu');let transparentClass='topMenu--transparent';if(topMenu.classList.contains(transparentClass)){window.addEventListener('scroll',()=>{console.log(window.scrollY)
if(window.scrollY>100){topMenu.classList.remove(transparentClass);}else{topMenu.classList.add(transparentClass);}})}}
topMenuAddFill();